import { useContext,useState,useEffect } from "react";
import { CartContext } from "../contexts/cart-context";

export default function Cart() {
  const { dispatch } = useContext(CartContext);
  const { itemsInCart } = useContext(CartContext);
  const [total,setTotal] = useState(0)
  useEffect(()=>
  {
    console.log("hi")
  //  let total=0;
    // let discount =0;
    itemsInCart.forEach(item=>
      {
        total +=  (item.markedprice -
          (item.discount / 100) * item.markedprice) *
        item.quantity;
        // discount +=  ((item.discount / 100) * item.markedprice)*
        // item.quantity
      })
      setTotal(total)
  },[itemsInCart])
  return (
    <>
      <div className="cartPage">
        <div className="cartPage__items">

        
        {(() => {
          if (itemsInCart.length === 0) return <p>Nothing in cart</p>;
          return (
            <div>
              <ul className="stacked ">
                {itemsInCart.map((item) => {
                  let flag = 0;
                  const price =
                    (item.markedprice -
                      (item.discount / 100) * item.markedprice) *
                    item.quantity;

                  if (item.quantity === 0) {
                    flag = 1;
                  }
                  return (
                    <li>
                      <div className="shoppingBag">
                        <img src={item.imgsrc} alt="" />
                        <button
                          className="primary-btn"
                          onClick={() => {
                            console.log("clicked", item);
                            dispatch({ type: "RemoveFromCart", payload: item });
                          }}
                        >
                          Remove
                        </button>
                      </div>
                      <div className="shoppingBag--information">
                        <div>
                          <h3>Brand X</h3>
                          <div>
                            <p> {item.text}</p>
                            <p>Quantity : {item.quantity}</p>
                            <button
                              className="quantity-btn"
                              onClick={() => {
                               
                                dispatch({ type: "Increase", payload: item });
                              }}
                            >
                              +
                            </button>
                            <button
                              className="quantity-btn"
                              disabled={flag === 0 ? false : true}
                              onClick={() =>
                                dispatch({ type: "Decrease", payload: item })
                              }
                            >
                              -
                            </button>
                          </div>
                        </div>

                        <div> Rs.{price}</div>
                      </div>
                    </li>
                  );
                })}
              </ul>
            </div>
          );
        })()}
        </div>
        <div className="cartPage__place">
            <div className="place__detail">
             <h2> PRICE DETAILS</h2>
            </div>
            <hr/>
            <div className="place__information">
              <ul className="place__information__list">
                <li>
                    <span>Price items</span>
                    <span> {total}</span>
                </li>
                <li>
                  <span>Discount</span>
                  <span>hi</span>
                </li>
                <li>
                    <span> Delivery charges</span>
                    <span>hello</span>
                </li>
              </ul>
            </div>
            <div className="place__total">
               <h2>Total Amount</h2>
               <h4>{total}</h4>
            </div>
            <div>
                You wil save {} on this order
            </div>
        </div>
      </div>
    </>
  );
}
